import{gsap}from"gsap";import{DrawSVGPlugin}from"gsap/DrawSVGPlugin";import{GSDevTools}from"gsap/GSDevTools";function simpleAnimation(){var a=gsap.timeline();return a.to("#line",{duration:1,drawSVG:"true"}).to("#small-circle",{duration:2,scale:2,x:350}).to("#large-circle",{duration:1,scale:.5,x:-350},"-=1"),a}function patternAnimation(){var a=gsap.timeline();return a.from(".square",{duration:1,drawSVG:0,stagger:.25,transformOrigin:"center"},"playPattern"),a}function toggleAnimation(){var a=gsap.timeline();return a.to("#happy-face",{duration:1,x:-100}),a}gsap.registerPlugin(DrawSVGPlugin,GSDevTools);var mainTl=gsap.timeline();mainTl.add(simpleAnimation()).add(patternAnimation()).add(toggleAnimation());